// Function to escape HTML for JSON
function escapeHtmlForJson(html) {
 return html
   .replace(/\\/g, '\\\\')    // Escape backslashes first
   .replace(/"/g, '\\"')      // Escape double quotes
   .replace(/\n/g, '\\n')     // Escape newlines
   .replace(/\r/g, '\\r')     // Escape carriage returns
   .replace(/\t/g, '\\t');    // Escape tabs
}

// Function to minify HTML
function minifyHtml(html) {
 return html
   .replace(/>\s+</g, '><')           // Remove whitespace between tags
   .replace(/\s+/g, ' ')              // Replace multiple spaces with single space
   .replace(/<!--[\s\S]*?-->/g, '')   // Remove HTML comments
   .trim();
}

// Your HTML content
const htmlTemplate = $input.first().json.extractedText;

// Get input data
const inputData = $input.all() || [{}];
const firstItem = inputData[0]?.json || {};

// Dynamic values
const dynamicTitle = firstItem.title || "Default Title";
const dynamicAuthorId = firstItem.authorId || 1;

// Process HTML
const minifiedHtml = minifyHtml(htmlTemplate);
const escapedHtml = escapeHtmlForJson(htmlTemplate);

// Return data
return [{
 title: dynamicTitle,
 htmlContent: htmlTemplate,
 htmlEscaped: escapedHtml,
 htmlMinified: minifiedHtml,
 authorId: dynamicAuthorId,
 slug: dynamicTitle.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)/g, ''),
 status: "publish"
}];
